		///   "XX                              ",
		///   "X.X                             ",
		///   "X..X                            ",
		///   "X...X                           ",
		///   "X....X                          ",
		///   "X.....X                         ",
		///   "X......X                        ",
		///   "X.......X                       ",
		///   "X........X                      ",
		///   "X.....XXXXX                     ",
		///   "X..X..X                         ",
		///   "X.X X..X                        ",
		///   "XX  X..X                        ",
		///   "X    X..X                       ",
		///   "     X..X                       ",
		///   "      X..X                      ",
		///   "      X..X                      ",
		///   "       XX                       ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "                                ",
		///   "0,0"
		/// };
		/// 
		/// SDL_Cursor *init_system_cursor(const char *image[])
		/// {
		///   int i, row, col;
		///   Uint8 data[4*32];
		///   Uint8 mask[4*32];
		///   int hot_x, hot_y;
		/// 
		///   i = -1;
		///   for (row=0; row&lt;32; ++row) {
		///     for (col=0; col&lt;32; ++col) {
		///       if (col % 8) {
		///         data[i] &lt;&lt;= 1;
		///         mask[i] &lt;&lt;= 1;
		///       } else {
		///         ++i;
		///         data[i] = mask[i] = 0;
		///       }
